import { fetchData } from '../../api/index';
import request from '@/utils/request';
export default {
    name: 'basetable',
    data() {
        return {
            editVisible: false,
            loginVisible: false,
            action: '',
            pagination: {
                currentPage: 1,
                pageSize: 5,
                pageTotal: 0,
            },
            tableData: [],
            multipleSelection: [],
            delList: [],
            EstablishedTime: '',
            NationName: '',
            options:[],
            selectValue: '',
            idx: -1,
            id: -1,
            dialogFormVisible: false,
            form: {},
            login: {
                username: '',
                password: '',
            },
            formLabelWidth: '90px',
            editform: '',
        };
    },
    created() {
        this.getData();
    },
    methods: {
        getData() {
            request.get('/api/provider').then(res => {
                console.log(res);
                this.tableData = res.Provider
            }, error => {
                console.log(error);
            });
        },
        getOption() {
            request.get('/api/nation').then(res => {
                console.log(res.Nation);
                this.options = []
                for (let i = 0; i < res.Nation.length; i++) {
                    this.options.push({'label': res.Nation[i].NationName, 'value': res.Nation[i].NationName})
                }
            }, error => {
                console.log(error);
            });
        },
        // 登录
        submitLogin() {
            request.post('//39.96.212.204:8081/api/login',
                            {'user': this.login.username, 'password': this.login.password},
            ).then (res => {
                this.$message.success('登录成功');
                localStorage.setItem('ms_username', this.login.username);
                this.loginVisible = false;
                this.getData();
            }, err => {                    
                if (err === 403) {
                    this.$message.error("账户名或密码错误");
                    this.login.username = '';
                    this.login.password = '';
                }
            })
        },
        // 删除操作
        handleDelete(index, row) {
            // 二次确认删除
            console.log(row.NationName)
            this.$confirm('确定要删除吗？', '提示', {
                type: 'warning'
            })
            .then(() => {
                request.delete('/api/provider',
                                {data: JSON.stringify(
                                    {'ProviderName': row.ProviderName,
                                     'NationName': row.NationName_id}
                                )}
                                ).then ( res => {
                                    console.log(res)
                                    this.$message.success('删除成功');
                                    this.getData();
                                }, err => {
                                    if (err === 403) {
                                        this.$message.error('未登录或登录已失效');
                                    }else if (err > 500) {
                                        this.$message.error('服务器错误');
                                    }

                                })
            })
        },
        // 打开ssh连接页面
        handleGo(index, row) {
            let ip = row.server_ip
            let url = `/ssh/${row.server_ip}/${Date.now().toString(35)}/${row.server_name}`;
            console.log(url);
            this.$router.push(url);
        },
        // 打开sftp连接页面
        handleDocument(index, row) {
            let ip = row.server_ip
            let url = `/sftp/${row.server_ip}/${row.server_name}`;
            console.log(url);
            this.$router.push(url);           
        },
        // 多选操作
        handleSelectionChange(val) {
            this.multipleSelection = val;
        },
        delAllSelection() {
            const length = this.multipleSelection.length;
            let str = '';
            this.delList = this.delList.concat(this.multipleSelection);
            for (let i = 0; i < length; i++) {
                str += this.multipleSelection[i].name + ' ';
            }
            this.$message.error(`删除了${str}`);
            this.multipleSelection = [];
        },
        // 编辑操作
        handleEdit(index, row) {
            this.action = '编辑'
            this.dialogFormVisible = true
            this.form = Object.assign({}, row)

        },
        // 分页导航
        handlePageChange(val) {
            this.pagination.currentPage = val
        },
        // 添加主机
        handleAdd() {
            this.action = '添加';
            this.dialogFormVisible = true;
            this.getOption()
        },
        submitHost() {
            request.put('/api/provider',
                        {'ProviderName': this.form.ProviderName,
                         'NationName': this.NationName, 
                         'EstablishedTime': this.form.EstablishedTime,
                         'Comment': this.form.Comment,
                        }, 
            ).then( res => {
                console.log(res);
                this.dialogFormVisible = false;
                this.$message.success('添加成功');
                this.getData();
            }, err =>{
                if (err === 403) {
                    this.$message.error('未登录或登录已失效');
                }
            });

        },
        testmessage()
        {
            this.$message.success('测试');
        }
    }
};
